import React from "react";
import { useEffect } from "react";
import Article from "./Article";
function ArticlesList({ articlename, titlesearch }) {
  const search = () => {
    return (articlename.filter((article)=> (article.title.toLowerCase)))
  }
  // const search = () => {
  //   return articlename.filter((film) => (film.title.toLowerCase().includes(titlesearch.toLowerCase()
    
  // };

  useEffect(() =>
    search() === [] ? document.write(<p>nothing found</p>) : null
  );

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-around",
        flexWrap: "wrap",
      }}
    >
      {search().map((article, index) => (
        <Article article={article} key={index} />
      ))}
    </div>
  );
}
export default ArticlesList;
