import React from "react";
import { Navbar, Form, FormControl, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import AddArticle from "./AddArticle";
import { Link } from "react-router-dom";

import "./Header.css";

function Header({ articlename, titlesearch, setarticlename, settitlesearch }) {
  const handleChange = (event) => settitlesearch(event.target.value);

  // <>
  //   <div className="top">
  //     <div className="topleft">
  //       <h1 className="logo">LovegoodBlog</h1>
  //     </div>
  //     <div className="topcenter">
  //       <ul className="toplist">
  //         <li className="toplistitem">HOME</li>
  //         <li className="toplistitem">ADD ARTICLE</li>
  //         <li className="toplistitem">CONTACT</li>
  //       </ul>
  //     </div>
  //     <div className="toprigh">
  //       <i className="topSearchIcon fa-solid fa-magnifying-glass"></i>
  //       <i className="topIcons fa-brands fa-facebook-square"></i>
  //       <i className="topIcons fa-brands fa-twitter-square"></i>
  //       <i className="topIcons fa-brands fa-instagram"></i>
  //     </div>
  //   </div>
  // </>
  const user = true;
  return (
    <div className="top">
      <div className="topLeft">
        <i className="topIcon fab fa-facebook-square"></i>
        <i className="topIcon fab fa-instagram-square"></i>
        <i className="topIcon fab fa-pinterest-square"></i>
        <i className="topIcon fab fa-twitter-square"></i>
      </div>
      <div className="topCenter">
        <ul className="topList">
          <li className="topListItem">
            <Link className="link" to="/">
              HOME
            </Link>
          </li>
          <li className="topListItem">ABOUT</li>
          <li className="topListItem">CONTACT</li>
          <li className="topListItem">
            <Link className="link" to="/write">
              WRITE
            </Link>
          </li>
          {user && <li className="topListItem">LOGOUT</li>}
        </ul>
      </div>
      <div className="topRight">
        {user ? (
          <Link className="link" to="/settings">
            <img
              className="topImg"
              src="https://images.pexels.com/photos/1858175/pexels-photo-1858175.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
              alt=""
            />
          </Link>
        ) : (
          <ul className="topList">
            <li className="topListItem">
              <Link className="link" to="/login">
                LOGIN
              </Link>
            </li>
          </ul>
        )}
        <i className="topSearchIcon fas fa-search"></i>
      </div>
    </div>
  );
}

export default Header;
