//     //       <i className="topIcons fa-brands fa-instagram"></i>
//     //     </div>
//     //   </div>
      <nav
        className=""
      >
    
      </nav>
      <Form className="d-flex">
        <FormControl
          type="search"
          placeholder=" Article Title"
          className="mr-2"
          aria-label="Search"
          onChange={handleChange}
          style={{ width: "200px" }}
          value={titlesearch}
        />
      </Form>
      <div
        className="rating-by-stars"
        style={{
          marginLeft: "50px",
          width: "500px",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-around",
        }}
      ></div>
      <AddArticle setarticlename={setarticlename} articlename={articlename} />
    </Navbar>
  );
}

