import React, { useState } from "react";
import { InputGroup, FormControl, Button, Modal, Form } from "react-bootstrap";

function AddArticle({ articlename, setarticletitle }) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [newTitle, setNewTitle] = React.useState("");
  const [newRate, setNewRate] = React.useState("");
  const [newPoster, setNewPoster] = React.useState("");
  const [newDescription, setNewDescription] = React.useState("");

  const NewArticle = () => {
    let newArticle = {
      id: Math.random(),
      title: `${newTitle}`,
      posterURL: `${newPoster}`,
      story: `${newStory}`,
    };

    setarticletitle([...articlename, NewArticle]);
    handleClose();
  };

  return (
    <>
      <Button
        variant="outline-dark"
        onClick={handleShow}
        style={{ marginLeft: "100px", width: "400px" }}
      >
        Add New Article
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Add Article</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Enter Article title
            </InputGroup.Text>
            <FormControl
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
              onChange={(e) => setNewTitle(e.target.value)}
            />
          </InputGroup>
          <br />

          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Enter Article PosterURL
            </InputGroup.Text>
            <FormControl
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
              onChange={(e) => setNewPoster(e.target.value)}
            />
          </InputGroup>
          <br />

          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Enter Your Story</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              onChange={(e) => setNewStory(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={NewArticle}>
            Publish
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default AddArticle;
